import React from "react"
import { TodoTitle } from'../ExerciseList/TodoTitle' // De esta manera es como importamos los componentes que estamos creando
import { TitleCreateExercise } from '../CreateExercise/CreateExercise'
import { CreateExercise } from'../CreateExercise/CreateExercise'
import { ImgExercise } from '../ExerciseList/ImgExercise.js'
import { SearchExercise } from '../ExerciseList/TodoItem.js'
import { TodoList } from'../ExerciseList/TodoList.js'
import { TodoItem } from'../ExerciseList/TodoItem.js'
import { PageLoading } from "./PageLoading/PageLoading"

function AppComponents({
    loading,
    error,
    createValue,
    setCreateValue,
    completedExercise,
    totalExercise,
    searchExercise,
    setSearchExercise,
    searchedExercise,
    exerciseCompleted,
    deltedExercise
}) {
    return(

        <React.Fragment> 
            <TitleCreateExercise />
            <CreateExercise
            createValue={createValue} // Despues de escribir el estado esta es la manera de concetar componentes hijos al componente padre
            setCreateValue={setCreateValue}
            />

            <TodoTitle completed={completedExercise} // Aca estoy utilizando el estado derivado creado anteriormente y es la manera de hacerlo un poco mas dinamico
            total={totalExercise} // Con otro estado derivado le estoy diciendo cuantos son el total y son lo que estan en el objeto defaultExercise
            />
            <SearchExercise
            searchExercise={searchExercise}
            setSearchExercise={setSearchExercise}
            />

            <TodoList>
            {loading && <PageLoading />}
            {error && <PageError />}
            {(!loading && searchedExercise.length == 0) && <p>NO HAY COINCIDENCIAS</p>}

            {searchedExercise.map(item => ( // Esta es la manera de trabajar con arrays, sus objetos y su manipulacion en react, despues de el componente, debemos indicar una key que concuerde con el objeto que se desea obtener. Al escribir el metodo para obetener el return no es en llaves si no en parentesis. Estoy ejecutando un estado derivado para poder filtrar el resultado que deseo
                <TodoItem 
                key={item.text} 
                text={item.text} 
                completed={item.completed}
                onCompleted={() => exerciseCompleted(item.text)} // Se debe ejecutar como una funcion dentro de otra funcion para que no se rompa la aplicacion
                onDeleted={() => deltedExercise(item.text)}
                />
            ))}
            <ImgExercise />
            </TodoList>
        </React.Fragment> // Esto es indispensable o sino el codigo se rompe, todos los componentes deben ir dentro de una etiqueta para poderse renderizar y este es la forma que nos da React para solucionar eso
    )
}
export { AppComponents }
