import React from "react"
import ReactDom from "react-dom"

const styleModal = {
    background: 'green',
    width: '300px',
}

function Modal({children}){
    return ReactDom.createPortal(
        <div style={styleModal}>
            {children}
        </div>,
        document.getElementById('modal')
    )
}
export { Modal }
