import { TodoTitle } from'../ExerciseList/TodoTitle' // De esta manera es como importamos los componentes que estamos creando
import { TitleCreateExercise } from '../CreateExercise/CreateExercise'
import { CreateExercise } from'../CreateExercise/CreateExercise'
import { ImgExercise } from '../ExerciseList/ImgExercise.js'
import { SearchExercise } from '../ExerciseList/TodoItem.js'
import { TodoList } from'../ExerciseList/TodoList.js'
import { TodoItem } from'../ExerciseList/TodoItem.js'
import { useLocalStorage } from './useLocalSotorage.js'
// import { BotonCreateExercise } from'./CreateExercise'
import React from 'react'

// LISTA DE EJERCICIOS

// const defaultExercise = [
//   {text: 'pecho plano', completed: false},
//   {text: 'pecho inclinado', completed:false},
//   {text: 'apertura de pecho', completed: false},
//   {text: 'paralelas', completed: false},
//   {text: 'trapecio', completed: false},
// ]
// localStorage.setItem('work-out', JSON.stringify(defaultExercise))

function App() { 

  // ESTADOS

  const [createValue, setCreateValue] = React.useState('') // Esta es la manera de crear un estado, con una variable que debe llevar entre corchetes dos parametros que seria el valor que se le este dando en tiempo real y el otro parametro seria el actualizador de el anterior estado
  //console.log('Agregar exercise ' + createValue) // De esta manera puedo visualizar el restultado en la consola
  const [exercises, saveExercises] = useLocalStorage('work-out', []) // Le estoy diciendo que el estado inicial es el array con objetos
  const [searchExercise, setSearchExercise] = React.useState('')
  //console.log(searchExercise)

  // ESTADOS DERIVADOS

  const completedExercise = exercises.filter(item => item.completed).length // Esto es un estado derivado, son variables, calculos etc, que hacemos a traves de un estado, tambien con el metodo filter le estoy diciendo que me devuelva los elementos que cumplan con la condicion
  const totalExercise = exercises.length

  const searchedExercise = exercises.filter((item)=>{
      return item.text.toLowerCase().includes(searchExercise.toLocaleLowerCase()) // Con estas dos metodos nuevos en el resultado no importa si lo busco en minuscula o mayuscula. Primero debo convertir tod en minuscula en con el metodo .toLowerCase() y despues lo que este entre parentesis del metodo includes le agrego el metodo .toLocaleLowerCase()
    })

  const exerciseCompleted = (text)=>{
    const newExercise =[...exercises]
     const exerciseIndex = newExercise.findIndex(
      (item) => item.text == text
     )
    newExercise[exerciseIndex].completed = true
    saveExercises(newExercise)
  }

  const deltedExercise = ()=>{
    const newExercise =[...exercises]
    newExercise.splice(exercises, 1)
    saveExercises(newExercise)
  }

  return (
    <React.Fragment> 
      <TitleCreateExercise />
      <CreateExercise
      createValue={createValue} // Despues de escribir el estado esta es la manera de concetar componentes hijos al componente padre
      setCreateValue={setCreateValue}
      />

      <TodoTitle completed={completedExercise} // Aca estoy utilizando el estado derivado creado anteriormente y es la manera de hacerlo un poco mas dinamico
      total={totalExercise} // Con otro estado derivado le estoy diciendo cuantos son el total y son lo que estan en el objeto defaultExercise
      />
      <SearchExercise
      searchExercise={searchExercise}
      setSearchExercise={setSearchExercise}
      />

      <TodoList>
       {searchedExercise.map(item => ( // Esta es la manera de trabajar con arrays, sus objetos y su manipulacion en react, despues de el componente, debemos indicar una key que concuerde con el objeto que se desea obtener. Al escribir el metodo para obetener el return no es en llaves si no en parentesis. Estoy ejecutando un estado derivado para poder filtrar el resultado que deseo
        <TodoItem 
        key={item.text} 
        text={item.text} 
        completed={item.completed}
        onCompleted={() => exerciseCompleted(item.text)} // Se debe ejecutar como una funcion dentro de otra funcion para que no se rompa la aplicacion
        onDeleted={() => deltedExercise(item.text)}
        // addedExercises={() => addedExercise(item.text)}
        />
       ))}
      <ImgExercise />
      </TodoList>
    </React.Fragment> // Esto es indispensable o sino el codigo se rompe, todos los componentes deben ir dentro de una etiqueta para poderse renderizar y este es la forma que nos da React para solucionar eso
  );
  } // En react los complementos de la pagina primero deben estar dentro de un div porque no se pueden devolver todos los componentes de una en el return y otra forma de hacer esto es con <React.Fragment></React.Fragment>

  export {App}
